const Discord = require("discord.js")
const client = new Discord.Client()
client.on("ready", () => {
  console.log(`Logged in as ${client.user.tag}!`)
})

client.on("message", (msg) => {
  if (msg.content === "ping") {
    msg.reply("Pong!")
  }
  if (msg.content === "+Hi") {
    msg.reply("Hello!!")
  }
  if (msg.content === "+hi") {
    msg.reply("Hello!!")
  }
  if (msg.content === "Ping") {
    msg.reply("Pong!")
  }

})

client.login("process.env.token");